import React from 'react';
import cx from 'classnames';
import { TableInstance } from 'react-table';
import Icon from 'src/components/Icon';

interface Props {
  getTableProps: (userProps?: any) => any;
  getTableBodyProps: (userProps?: any) => any;
  prepareRow: TableInstance['prepareRow'];
  headerGroups: TableInstance['headerGroups'];
  rows: TableInstance['rows'];
  loading: boolean;
}
export default function TableCollection({
  getTableProps,
  getTableBodyProps,
  prepareRow,
  headerGroups,
  rows,
  loading,
}: Props) {
  return (
    <table {...getTableProps()} className="table table-hover">
      <thead>
        {headerGroups.map(headerGroup => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(column => {
              let sortIcon = <Icon name="sort" />;
              if (column.isSortedDesc) {
                sortIcon = <Icon name="sort-desc" />;
              } else if (!column.isSortedDesc) {
                sortIcon = <Icon name="sort-asc" />;
              }

              return column.hidden ? null : (
                <th
                  {...column.getHeaderProps(
                    column.sortable ? column.getSortByToggleProps() : {},
                  )}
                  data-test="sort-header"
                >
                  <span>{column.render('Header')}</span>
                  {column.sortable && (
                    <span className="sort-icon">{sortIcon}</span>
                  )}
                </th>
              );
            })}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map(row => {
          prepareRow(row);
          return (
            <tr
              {...row.getRowProps()}
              className={cx({
                'table-row-loader': loading,
                'table-row-selected': row.isSelected,
              })}
              onMouseEnter={() => row.setState && row.setState({ hover: true })}
              onMouseLeave={() =>
                row.setState && row.setState({ hover: false })
              }
            >
              {row.cells.map(cell => {
                if (cell.column.hidden) return null;

                const columnCellProps = cell.column.cellProps || {};

                return (
                  <td
                    className="table-cell"
                    {...cell.getCellProps()}
                    {...columnCellProps}
                  >
                    <span>{cell.render('Cell')}</span>
                  </td>
                );
              })}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
}
