import React, { ReactNode } from 'react';
// @ts-ignore
import { OverlayTrigger, Tooltip } from 'react-bootstrap';

interface Props {
  children?: ReactNode;
  className?: string;
  href?: string;
  onClick?: () => void;
  placement?: string;
  style?: object;
  tooltip?: string | null;
}

const Link = ({
  children = null,
  className = '',
  href = '#',
  onClick = () => undefined,
  placement = 'top',
  style = {},
  tooltip = null,
}: Props) => {
  const link = (
    <a
      href={href}
      onClick={onClick}
      style={style}
      className={'Link ' + className}
    >
      {children}
    </a>
  );
  if (tooltip) {
    return (
      <OverlayTrigger
        overlay={<Tooltip id="tooltip">{tooltip}</Tooltip>}
        placement={placement}
        delayShow={300}
        delayHide={150}
      >
        {link}
      </OverlayTrigger>
    );
  }
  return link;
};

export default Link;
